DROP TABLE IF EXISTS Programs;
CREATE TABLE Programs(
	ProgramCode VARCHAR(4) primary key,
	ProgramDescription VARCHAR(60) NOT NULL,
	SchoolCode VARCHAR(2) NOT NULL,
    Dipolmacode VARCHAR(8) NOT NULL,
	FOREIGN KEY (SchoolCode) REFERENCES Schools(SchoolCode)
);
